 */
@Slf4j
@Configuration
@AllArgsConstructor
@ConditionalOnClass(com.ctrip.framework.apollo.Apollo.class)
public class ApolloConfig {
    private final ApplicationEventPublisher eventPublisher;

    /**
     * 自动更新环境变量
